use image::GrayImage;
use rayon::prelude::*;

pub fn binarize(img: &GrayImage) -> Option<GrayImage> {
    let mut output_buffer = vec![0u8; img.len()];
    output_buffer.par_iter_mut().zip(img.par_iter()).for_each(|(out, &dec)| if dec > 127 { *out = 255 });

    GrayImage::from_vec(img.width(), img.height(), output_buffer)
}

pub fn gradient_vertical(img: &GrayImage) -> Vec<i16> {
    let width = img.width() as usize;
    let img = img.as_raw();
    let mut gradient = vec![0i16; img.len()];

    gradient.par_iter_mut().enumerate().for_each(|(i, val)| {
        let col = i % width;
        if col != 0 {
            *val = img[i] as i16 - img[i - 1] as i16;
        }
    });

    gradient
}


